const data = $json.body || $json;

return {
  company: data.company || 'Unknown_Company',
  title: data.title || 'Unknown_Title',
  description: data.description || '',
  url: data.url || '',
  location: data.location || '',
  application_number: data.application_number || Date.now()
};

const resumeData = $input.first().json;
const jobData = $('Counter').first().json;

// Get HTML - adjust based on Simplify Output setting
let resumeHtml = resumeData.message || resumeData.content?.[0]?.text || resumeData.text || JSON.stringify(resumeData);

// Clean markdown code blocks
resumeHtml = resumeHtml.replace(/```html\n?/gi, '').replace(/```\n?/gi, '').trim();

// Ensure valid HTML wrapper
if (!resumeHtml.toLowerCase().includes('<!doctype') && !resumeHtml.toLowerCase().includes('<html')) {
  resumeHtml = `<!DOCTYPE html><html><head><meta charset="UTF-8"><title>Resume - Brandon Ruiz</title></head><body>${resumeHtml}</body></html>`;
}

// Clean company name for filename
const company = (jobData.company || 'Unknown')
  .replace(/[^a-zA-Z0-9\s]/g, '')
  .replace(/\s+/g, '_')
  .substring(0, 30);

const appNum = jobData.application_number;

// Create binary data for Gotenberg
const resumeBinary = await this.helpers.prepareBinaryData(
  Buffer.from(resumeHtml, 'utf-8'),
  'index.html',
  'text/html'
);

return [{
  json: {
    company: company,
    application_number: appNum,
    resume_filename: `${company}_${appNum}_Resume.pdf`
  },
  binary: {
    data: resumeBinary
  }
}];

